          "and": [
            {
              "greater": [
                "@length(body('ListAllSensorJobItembatchAttributeMappings')?['value'])",
                1
              ]
            }
          ]
        },
        "runAfter": {
          "ListAllSensorJobItembatchAttributeMappings": [
            "Succeeded"
          ]
        },
        "type": "If"
      },
