          "where": "@and(equals(item()['IsSensorActiveForScenario'], 'Yes'), equals(item()['Scenario'], 'AssetMaintenance'))"
        },
        "runAfter": {
          "ParseScenarioSensorMappings": [
            "Succeeded"
          ]
        },
